import { Component, React } from "react";
import ReactDOM from 'react-dom';
import { BrowserRouter as Router } from 'react-router-dom';
//import './infoApp.css';
import { MDBCard, MDBCardBody, MDBContainer,MDBCardTitle,MDBCardText } from "mdbreact";
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import data from './data';

function InfoApp () {
  const [currentInfo, setCurrentInfo] = useState({
    "id": 0,
    "application": "http://demo.opslinesys.net",
    "alert": "",
    "severity_level": 0,
    "name": "Click a Button to Learn About Various Attack Techniques",
    "description": "",
    "solution": "",
    "evidence": ""});

  const [occurrences, setOccurrences] = useState(0);


  const searchDB = (searchTerm) => {
      let found = [];
      for (let i = 0; i < data.length; i++){
          if (data[i].name === searchTerm){
              found.push(data[i]);
          }
      }
      console.log(found);
      let occurrences = found.length;
      this.setState({occurrences: occurrences});
      this.setState({currentInfo: found[0]});

      //grab list of json objects from db
      //search list using .find (returns all occurences in a list)
      //     can do a length() on this to get occurences

  }

  const displayInfo = () => {
    if (this.state.currentInfo.id == 0){
        //display "Click a Button to Learn About Various Attack Techniques" title
        const result = <h1>{this.state.currentInfo.name}</h1>
    } else if (this.state.currentInfo.evidence == ""){
        const result = (<div className = "info-card-row">
            <MDBCard className = "info-card-body">
                <p className="infoTitle">{this.state.currentInfo.name}</p>
                <p className="infoCard">Severity Level: {this.state.currentInfo.severity_level}</p>
                <p className="infoCard">Alert: {this.state.currentInfo.alert}</p>
                <p className="infoCard">Description: {this.state.currentInfo.description}</p>
                <p className="infoCard">Solution: {this.state.currentInfo.solution}</p>
                <p className="infoCard">Evidence: No evidence included</p>
                <p className="infoCard">Application: {this.state.currentInfo.application}</p>
                <p className="infoCard">Occurrences: {this.state.currentInfo.occurrences}</p>
            </MDBCard>
        </div>)
    } else {
        //display state elements. If we reach this that means a button has been pressed (otherwise id == 0)
        const result = (<div className = "info-card-row">
            <MDBCard className = "info-card-body">
                <p className="infoTitle">{this.state.currentInfo.name}</p>
                <p className="infoCard">Severity Level: {this.state.currentInfo.severity_level}</p>
                <p className="infoCard">Alert: {this.state.currentInfo.alert}</p>
                <p className="infoCard">Description: {this.state.currentInfo.description}</p>
                <p className="infoCard">Solution: {this.state.currentInfo.solution}</p>
                <p className="infoCard">Evidence: {this.state.currentInfo.evidence}</p>
                <p className="infoCard">Application: {this.state.currentInfo.application}</p>
                <p className="infoCard">Occurrences: {this.state.currentInfo.occurrences}</p>
            </MDBCard>
        </div>)
    }
    return result;
  }
  return(
    <div>
        <h1>InfoApp</h1>
        <Row>
            <Col>
                <Button onClick = {() => {this.searchDB("Path Traversal")}}>
                    Path Traversal
                </Button>
                <Button onClick = {() => {this.searchDB("Cross Site Scripting")}}>
                    Cross Site Scripting
                </Button>
                <Button onClick = {() => {this.searchDB("X-Frame-Options Header Not Set")}}>
                    X-Frame-Options Header Not Set
                </Button>
                <Button onClick = {() => {this.searchDB("Server Leaks Information via \"X-Powered-By\" HTTP Response Header Field(s)")}}>
                    Server Leaks Information via \"X-Powered-By\" HTTP Response Header Field(s)
                </Button>
                <Button onClick = {() => {this.searchDB("Cross-Domain JavaScript Source File Inclusion")}}>
                    Cross-Domain JavaScript Source File Inclusion
                </Button>
                <Button onClick = {() => {this.searchDB("Absence of Anti-CSRF Tokens")}}>
                    Absence of Anti-CSRF Tokens
                </Button>
                <Button onClick = {() => {this.searchDB("X-Content-Type-Options Header Missing")}}>
                    X-Content-Type-Options Header Missing
                </Button>
            </Col>
            <Col>
                {this.displayInfo}
            </Col>
        </Row>
    </div>
  )
}


export default InfoApp;
