import { Component, React, useState } from "react";
import ReactDOM from 'react-dom';
import { BrowserRouter as Router } from 'react-router-dom';
//import './infoApp.css';
import { MDBCard, MDBCardBody, MDBContainer,MDBCardTitle,MDBCardText } from "mdbreact";
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import data from './data';

function InfoApp () {
  const [currentInfo, setCurrentInfo] = useState({
    "id": 0,
    "application": "http://demo.opslinesys.net",
    "alert": "",
    "severity_level": 0,
    "name": "Click a Button to Learn About Various Attack Techniques",
    "description": "",
    "solution": "",
    "evidence": ""});

  const [occurrences, setOccurrences] = useState(0);

  const searchDB = (searchTerm) => {
      let found = [];
      for (let i = 0; i < data.length; i++){
          if (data[i].name === searchTerm){
              found.push(data[i]);
          }
      }
      console.log(found);
      setOccurrences(found.length);
      setCurrentInfo(found[0]);

      //grab list of json objects from db
      //search list using .find (returns all occurences in a list)
      //     can do a length() on this to get occurences

  }

  const NewlineText = (inString) => {
    const newText = inString.split('\n').map(str => <p key = {Math.random().toString(36).substr(2, 9)}>{str}</p>);

    return newText;
  }

  const displayInfo = () => {
    let result;
    if (currentInfo.id == 0){
        //display "Click a Button to Learn About Various Attack Techniques" title
        result = <h1>{currentInfo.name}</h1>
    } else if (currentInfo.evidence == ""){
        result = (<div className = "info-card-row">
            <MDBCard className = "info-card-body">
                <h1 className="infoTitle">Name: {currentInfo.name}</h1>
                <h2 className="infoCard">Severity Level: {currentInfo.severity_level}</h2>
                <h3 className="infoCard">Alert: {currentInfo.alert}</h3>
                <h4 className="infoCard">Description: {NewlineText(currentInfo.description)}</h4>
                <h5 className="infoCard">Solution: {NewlineText(currentInfo.solution)}</h5>
                <h6 className="infoCard">Evidence: No evidence included</h6>
                <h7 className="infoCard">Application: {currentInfo.application}</h7>
                <h8 className="infoCard">Occurrences: {occurrences}</h8>
            </MDBCard>
        </div>)
    } else {
        //display state elements. If we reach this that means a button has been pressed (otherwise id == 0)
        result = (<div className = "info-card-row">
            <MDBCard className = "info-card-body">
                <h1 className="infoTitle">{currentInfo.name}</h1>
                <h2 className="infoCard">Severity Level: {currentInfo.severity_level}</h2>
                <h3 className="infoCard">Alert: {currentInfo.alert}</h3>
                <h4 className="infoCard">Description: {NewlineText(currentInfo.description)}</h4>
                <h5 className="infoCard">Solution: {NewlineText(currentInfo.solution)}</h5>
                <h6 className="infoCard">Evidence: {currentInfo.evidence}</h6>
                <h7 className="infoCard">Application: {currentInfo.application}</h7>
                <h8 className="infoCard">Occurrences: {occurrences}</h8>
            </MDBCard>
        </div>)
    }
    return result;
  }
  return(
    <div>
        <h1>InfoApp</h1>
        <Row>
            <Col>
                <Button onClick = {() => {searchDB("Path Traversal")}}>
                    Path Traversal
                </Button>
                <Button onClick = {() => {searchDB("Cross Site Scripting (DOM Based)")}}>
                    Cross Site Scripting
                </Button>
                <Button onClick = {() => {searchDB("X-Frame-Options Header Not Set")}}>
                    X-Frame-Options Header Not Set
                </Button>
                <Button onClick = {() => {searchDB("Server Leaks Information via \"X-Powered-By\" HTTP Response Header Field(s)")}}>
                    Server Leaks Information via \"X-Powered-By\" HTTP Response Header Field(s)
                </Button>
                <Button onClick = {() => {searchDB("Cross-Domain JavaScript Source File Inclusion")}}>
                    Cross-Domain JavaScript Source File Inclusion
                </Button>
                <Button onClick = {() => {searchDB("Absence of Anti-CSRF Tokens")}}>
                    Absence of Anti-CSRF Tokens
                </Button>
                <Button onClick = {() => {searchDB("X-Content-Type-Options Header Missing")}}>
                    X-Content-Type-Options Header Missing
                </Button>
            </Col>
            <Col>
                {displayInfo()}
            </Col>
        </Row>
    </div>
  )
}

export default InfoApp;
